def peristeras_rgb():
    
    c = mcolors.ColorConverter().to_rgb
    
    rgb = [c('lightskyblue'),
           c('dodgerblue'),
           c('teal'),
           c('limegreen'),
           c('yellow'),
           c('orange'),
           c('orangered'),
           c('red'), 
           c('firebrick'),
           c('darkred'),
           c('k')]

    rgb = np.array(rgb)
    
    return(rgb)

def peristeras2_rgb():
    c = mcolors.ColorConverter().to_rgb

    rgb = [c('darkblue'),
           c('royalblue'),
           c('dodgerblue'),
           c('cornflowerblue'),
           c('yellowgreen'),
           c('yellow'),
           c('orange'),
           c('orangered'),
           c('darkred')]

    rgb = np.array(rgb)
    
    return(rgb)
